import { Component, OnInit, Input } from '@angular/core';
import { Product } from '../products';
// import ()

@Component({
  selector: 'app-product-item',
  templateUrl: './product-item.component.html',
  styleUrls: ['./product-item.component.css']
})
export class ProductItemComponent implements OnInit{
  ngOnInit(): void {}


  @Input() item: Product;
  @Input() cur_category: string | undefined;
  visible = true;

  constructor() {
    this.item = new Product;
    this.visible = true;

  }
}
<div *ngIf="cur_category === item.categor"  class="product">
  <h3>
    <a [title]="item.name + ' details'"
    [routerLink]="['/products', item.id]">
      {{ item.name }}
    </a>
  </h3>

  <div class="product-img">
    <div >
      <a href="{{item.src}}">
        <img src="{{item.image[0]}}" class="mainImg">
      </a>
    </div>

    <div  class="small-cont">
      <img src="{{pImg}}" class="small-img" *ngFor="let pImg of item.image" >
    </div>
  </div>

  <p *ngIf="item.description">
    Description: {{ item.description }}
  </p>

  <button class="like">
    {{item.likes}}
  </button>

  <a href="https://telegram.me/share/url?url={{item.src}}&text=Check this product" >
    <button type="button" >
      Share
    </button>
  </a>
</div>

