import { Component, OnInit, Input, EventEmitter, Output } from '@angular/core';
import { Product } from '../products';
// import ()

@Component({
  selector: 'app-product-item',
  templateUrl: './product-item.component.html',
  styleUrls: ['./product-item.component.css']
})
export class ProductItemComponent implements OnInit{
  ngOnInit(): void {}


  @Input() item: Product;
  @Input() cur_category: string | undefined;
  @Output() remove = new EventEmitter();
  visible = true;

  constructor() {
    this.item = new Product;
    this.visible = true;
  }
  liked(){
    this.item.likes +=1;
  }
  // removeItem() {
  //   this.visible = false;
  // }
  // removeTask() {
//   this.remove.emit(this.task.id);
// }
}

// removeTask() {
//   this.remove.emit(this.task.id);
// }


<div *ngIf="cur_category === item.categor && visible"  class="product" >
  <h3>
    <a [title]="item.name + ' details'"
    [routerLink]="['/products', item.id]">
      {{ item.name }}
    </a>
  </h3>

  <div class="product-img">
    <div >
      <a href="{{item.src}}">
        <img src="{{item.image[0]}}" class="mainImg">
      </a>
    </div>

    <div  class="small-cont">
      <img src="{{pImg}}" class="small-img" *ngFor="let pImg of item.image" >
    </div>
  </div>

  <p *ngIf="item.description">
    Description: {{ item.description }}
  </p>

  <div class="but-cont">
    <button class="like" (click)="liked()" type="button">
      <img src="https://cdn-icons-png.flaticon.com/512/3179/3179453.png" alt="">
      {{item.likes}}
    </button>


    <a href="https://telegram.me/share/url?url={{item.src}}&text=Check this product" >
      <button type="button">
          Share
      </button>
    </a>
    <button (click)="removeItem()">
      remove item
    </button>
  </div>


</div>

