import { Component, OnInit, Input } from '@angular/core';
import { Product, products } from '../products';

@Component({
  selector: 'app-product-item',
  templateUrl: './product-item.component.html',
  styleUrls: ['./product-item.component.css']
})
export class ProductItemComponent implements OnInit{
  ngOnInit(): void {}

  @Input() item: Product;

  constructor() {
    this.item = new Product;
  }
}


  <h3>
    <a [title]="this.name + ' details'"
    [routerLink]="['/products', product.id]">
      {{ product.name }}
    </a>
  </h3>

  <div class="product-img">
    <div >
      <a href="{{product.src}}">
        <img src="{{product.image[0]}}" class="mainImg">
      </a>
    </div>

    <div  class="small-cont">
      <img src="{{pImg}}" class="small-img" *ngFor="let pImg of product.image" >
    </div>
</div>

  <p *ngIf="product.description">
    Description: {{ product.description }}
  </p>


  <a href="https://telegram.me/share/url?url={{product.src}}&text=Check this product" >
    <button type="button" >
      Share
    </button>
  </a>

  <app-product-alerts
    [product]="product"
    (notify)="onNotify()">
  </app-product-alerts>
